import React, { useState, useEffect } from "react";
import { useMsal } from "@azure/msal-react";
import axios from "axios";

const App: React.FC = () => {
  const [token, setToken] = useState<string | null>(null);
  const [protectedData, setProtectedData] = useState<string | null>(null);
  const { instance } = useMsal();

  // Extract token from URL on load
  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const jwtToken = urlParams.get("token");
    if (jwtToken) {
      setToken(jwtToken);
      localStorage.setItem("token", jwtToken);

      // Remove token from URL (clean up)
      window.history.replaceState({}, document.title, "/");
    }
  }, []);

  const handleGoogleLogin = () => {
    window.location.href = "https://localhost:7104/api/auth/google-login";
  };

  const handleMicrosoftLogin = async () => {
    try {
      const loginResponse = await instance.loginPopup();
      const accessToken = loginResponse.accessToken;

      const { data } = await axios.post(
        "https://localhost:7104/api/auth/microsoft-callback",
        {},
        {
          headers: {
            Authorization: `Bearer ${accessToken}`,
          },
        }
      );

      setToken(data.token);
      localStorage.setItem("token", data.token);
    } catch (error) {
      console.error("Microsoft Auth Error:", error);
    }
  };

  const handleProtectedData = async () => {
    try {
      const storedToken = localStorage.getItem("token");
      if (!storedToken) {
        console.error("No token found, please log in first.");
        return;
      }

      const { data } = await axios.get("https://localhost:7104/api/protected", {
        headers: {
          Authorization: `Bearer ${storedToken}`,
        },
      });

      setProtectedData(data.Message);
    } catch (error) {
      console.error("Protected API Error:", error);
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h2>OAuth Login</h2>

      <h3>Google Login</h3>
      <button onClick={handleGoogleLogin}>Login with Google</button>

      <h3>Microsoft Login</h3>
      <button onClick={handleMicrosoftLogin}>Login with Microsoft</button>

      {token && (
        <div>
          <h3>JWT Token:</h3>
          <textarea value={token} readOnly rows={5} style={{ width: "80%" }} />
        </div>
      )}

      <h3>Protected API</h3>
      <button onClick={handleProtectedData}>Get Protected Data</button>

      {protectedData && (
        <div>
          <h3>Protected API Response:</h3>
          <pre>{protectedData}</pre>
        </div>
      )}
    </div>
  );
};

export default App;
