import React, { useEffect, useState } from "react";
import { GoogleLogin } from "@react-oauth/google";
import { useMsal } from "@azure/msal-react";
import axios from "axios";

const App: React.FC = () => {
  const [token, setToken] = useState<string | null>(localStorage.getItem("token"));
  const [protectedData, setProtectedData] = useState<string | null>(null);
  const { instance } = useMsal();

  const handleGoogleLogin = () => {
    window.location.href = "https://localhost:7104/api/auth/google-login";
  };

  const handleMicrosoftLogin = async () => {
    try {
      const loginResponse = await instance.loginPopup();
      const accessToken = loginResponse.accessToken;

      const { data } = await axios.post(
        "https://localhost:7104/api/auth/microsoft-callback",
        {},
        { headers: { Authorization: `Bearer ${accessToken}` } }
      );

      localStorage.setItem("token", data.token);
      setToken(data.token);
    } catch (error) {
      console.error("Microsoft Auth Error:", error);
    }
  };

  useEffect(() => {
    if (token) {
      axios
        .get("https://localhost:7104/api/protected", {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((response) => setProtectedData(response.data.Message))
        .catch((error) => console.error("Protected API Error:", error));
    }
  }, [token]);

  return (
    <div className="container d-flex justify-content-center align-items-center vh-100">
      <div className="card p-4 shadow-lg text-center">
        <h2 className="mb-4">OAuth Login</h2>

        {!token ? (
          <>
            <button className="btn btn-primary mb-3" onClick={handleGoogleLogin}>
              Login with Google
            </button>
            <button className="btn btn-info" onClick={handleMicrosoftLogin}>
              Login with Microsoft
            </button>
          </>
        ) : (
          <h3 className="text-success">{protectedData || "Loading..."}</h3>
        )}
      </div>
    </div>
  );
};

export default App;
