import React, { useState } from "react";
import { GoogleLogin, CredentialResponse } from "@react-oauth/google";
import { useMsal } from "@azure/msal-react";
import axios from "axios";

const App: React.FC = () => {
  const [token, setToken] = useState<string | null>(localStorage.getItem("token"));
  const [protectedData, setProtectedData] = useState<any>(null);
  const { instance } = useMsal();

  const handleGoogleSuccess = async (response: CredentialResponse) => {
    try {
      if (!response.credential) {
        console.error("No credential received from Google");
        return;
      }

      const { data } = await axios.post(
        "https://localhost:7104/api/auth/google-callback",
        { token: response.credential },
        { headers: { "Content-Type": "application/json" } }
      );

      console.log("JWT Token:", data.token);
      localStorage.setItem("token", data.token);
      setToken(data.token);
    } catch (error) {
      console.error("Google Auth Error:", error);
    }
  };

  const handleGoogleFailure = () => {
    console.error("Google Login Failed");
  };

  const handleMicrosoftLogin = async () => {
    try {
      const loginResponse = await instance.loginPopup();
      const backendResponse = await axios.post("https://localhost:7104/api/auth/microsoft-callback", {
        token: loginResponse.accessToken,
      });

      localStorage.setItem("token", backendResponse.data.Token);
      setToken(backendResponse.data.Token);
    } catch (error) {
      console.error("Microsoft Auth Error:", error);
    }
  };

  const handleProtectedData = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        console.error("No token found, please log in first.");
        return;
      }

      const { data } = await axios.get("https://localhost:7104/api/protected", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      setProtectedData(data);
    } catch (error) {
      console.error("Protected API Error:", error);
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h2>OAuth Login</h2>

      <h3>Google Login</h3>
      <GoogleLogin onSuccess={handleGoogleSuccess} onError={handleGoogleFailure} />

      <h3>Microsoft Login</h3>
      <button onClick={handleMicrosoftLogin}>Login with Microsoft</button>

      {token && (
        <div>
          <h3>JWT Token:</h3>
          <textarea value={token} readOnly rows={5} style={{ width: "80%" }} />
        </div>
      )}

      <h3>Protected API</h3>
      <button onClick={handleProtectedData}>Get Protected Data</button>

      {protectedData && (
        <div>
          <h3>Protected API Response:</h3>
          <pre>{JSON.stringify(protectedData, null, 2)}</pre>
        </div>
      )}
    </div>
  );
};

export default App;
