import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Dashboard: React.FC = () => {
  const [message, setMessage] = useState<string | null>(null);
  const navigate = useNavigate();

  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const tokenFromUrl = urlParams.get("token");
    const storedToken = localStorage.getItem("token");

    if (tokenFromUrl) {
      localStorage.setItem("token", tokenFromUrl);
      navigate("/dashboard");
    }

    const token = tokenFromUrl || storedToken;

    if (!token) {
      navigate("/");
      return;
    }

    axios
      .get("https://localhost:7104/api/protected", {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((response) => setMessage(response.data.Message))
      .catch((error) => console.error("Protected API Error:", error));
  }, [navigate]);

  return (
    <div className="d-flex justify-content-center align-items-center vh-100 bg-light">
      <div className="card p-4 shadow-lg text-center" style={{ width: "400px" }}>
        <h2 className="mb-4">Welcome</h2>
        {message ? <p className="alert alert-success">{message}</p> : <p>Loading...</p>}
      </div>
    </div>
  );
};

export default Dashboard;
