import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Route, Routes, useNavigate } from "react-router-dom";
import { GoogleLogin } from "@react-oauth/google";
import { useMsal } from "@azure/msal-react";
import axios from "axios";
import Dashboard from "./Dashboard";

const Login: React.FC = () => {
  const { instance } = useMsal();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Redirect if already logged in
    const token = localStorage.getItem("token");
    if (token) {
      navigate("/dashboard");
    } else {
      setLoading(false);
    }
  }, [navigate]);

  if (loading) return <div className="text-center mt-5">Loading...</div>;

  const handleGoogleLogin = () => {
    window.location.href = "https://localhost:7104/api/auth/google-login";
  };

  const handleMicrosoftLogin = async () => {
    try {
      const loginResponse = await instance.loginPopup();
      const accessToken = loginResponse.accessToken;

      const { data } = await axios.post(
        "https://localhost:7104/api/auth/microsoft-callback",
        {},
        {
          headers: { Authorization: `Bearer ${accessToken}` },
        }
      );

      localStorage.setItem("token", data.token);
      navigate("/dashboard");
    } catch (error) {
      console.error("Microsoft Auth Error:", error);
    }
  };

  return (
    <div className="d-flex justify-content-center align-items-center vh-100 bg-light">
      <div className="card p-4 shadow-lg text-center" style={{ width: "350px" }}>
        <h2 className="mb-4">Login</h2>
        <input type="text" className="form-control mb-2" placeholder="Username" disabled />
        <input type="password" className="form-control mb-3" placeholder="Password" disabled />
        <button className="btn btn-primary w-100 mb-2" disabled>Login</button>
        <button className="btn btn-danger w-100 mb-2" onClick={handleGoogleLogin}>Login with Google</button>
        <button className="btn btn-info w-100" onClick={handleMicrosoftLogin}>Login with Microsoft</button>
      </div>
    </div>
  );
};

const App: React.FC = () => {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Login />} />
        <Route path="/dashboard" element={<Dashboard />} />
      </Routes>
    </Router>
  );
};

export default App;
