import React, { useState, useEffect } from "react";
import { GoogleLogin } from "@react-oauth/google";
import { useMsal } from "@azure/msal-react";
import axios from "axios";

const App: React.FC = () => {
  const [token, setToken] = useState<string | null>(null);
  const [protectedData, setProtectedData] = useState<string | null>(null);
  const { instance } = useMsal();

  useEffect(() => {
    const storedToken = localStorage.getItem("token");
    if (storedToken) {
      setToken(storedToken);
      fetchProtectedData(storedToken);
    }
  }, []);

  const fetchProtectedData = async (jwtToken: string) => {
    try {
      const { data } = await axios.get("https://localhost:7104/api/protected", {
        headers: { Authorization: `Bearer ${jwtToken}` },
      });
      setProtectedData(data.message);
    } catch (error) {
      console.error("Error fetching protected data:", error);
    }
  };

  const handleGoogleLogin = () => {
    window.location.href = "https://localhost:7104/api/auth/google-login";
  };

  const handleMicrosoftLogin = async () => {
    try {
      const loginResponse = await instance.loginPopup();
      const accessToken = loginResponse.accessToken;

      const { data } = await axios.post(
        "https://localhost:7104/api/auth/microsoft-callback",
        {},
        {
          headers: {
            Authorization: `Bearer ${accessToken}`,
          },
        }
      );

      setToken(data.token);
      localStorage.setItem("token", data.token);
      fetchProtectedData(data.token);
    } catch (error) {
      console.error("Microsoft Auth Error:", error);
    }
  };

  return (
    <div className="flex items-center justify-center h-screen bg-gray-100">
      <div className="bg-white p-8 rounded-lg shadow-lg w-96 text-center">
        {!token ? (
          <>
            <h2 className="text-2xl font-semibold mb-4">Login</h2>
            <input
              type="text"
              placeholder="Username"
              className="w-full px-4 py-2 mb-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              disabled
            />
            <input
              type="password"
              placeholder="Password"
              className="w-full px-4 py-2 mb-4 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              disabled
            />
            <button
              className="w-full bg-red-500 text-white py-2 rounded-md mb-2 hover:bg-red-600"
              onClick={handleGoogleLogin}
            >
              Login with Google
            </button>
            <button
              className="w-full bg-blue-600 text-white py-2 rounded-md hover:bg-blue-700"
              onClick={handleMicrosoftLogin}
            >
              Login with Microsoft
            </button>
          </>
        ) : (
          <div className="text-lg font-medium text-green-600">{protectedData || "Loading protected data..."}</div>
        )}
      </div>
    </div>
  );
};

export default App;
