import React, { useEffect, useState } from "react";
import { useMsal } from "@azure/msal-react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";

const App: React.FC = () => {
  const [token, setToken] = useState<string | null>(null);
  const [protectedData, setProtectedData] = useState<string | null>(null);
  const { instance } = useMsal();

  useEffect(() => {
    // Extract token from URL if present
    const urlParams = new URLSearchParams(window.location.search);
    const tokenFromUrl = urlParams.get("token");

    if (tokenFromUrl) {
      localStorage.setItem("token", tokenFromUrl);
      setToken(tokenFromUrl);

      // Remove token from URL after storing it
      window.history.replaceState({}, document.title, "/");
    } else {
      // Check if token exists in localStorage
      const storedToken = localStorage.getItem("token");
      if (storedToken) {
        setToken(storedToken);
      }
    }
  }, []);

  useEffect(() => {
    if (token) {
      fetchProtectedData();
    }
  }, [token]);

  const fetchProtectedData = async () => {
    try {
      const response = await axios.get("https://localhost:7104/api/protected", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setProtectedData(response.data.Message);
    } catch (error) {
      console.error("Error fetching protected data:", error);
    }
  };

  const handleGoogleLogin = () => {
    window.location.href = "https://localhost:7104/api/auth/google-login";

    setTimeout(() => {
      window.location.reload();
    }, 2000); // Refresh after 2 seconds
  };

  const handleMicrosoftLogin = async () => {
    try {
      const loginResponse = await instance.loginPopup();
      const accessToken = loginResponse.accessToken;

      const { data } = await axios.post(
        "https://localhost:7104/api/auth/microsoft-callback",
        {},
        { headers: { Authorization: `Bearer ${accessToken}` } }
      );

      localStorage.setItem("token", data.token);
      setToken(data.token);
    } catch (error) {
      console.error("Microsoft Auth Error:", error);
    }
  };

  if (protectedData) {
    return (
      <div className="container d-flex justify-content-center align-items-center vh-100">
        <div className="card p-4 text-center">
          <h2>Protected Content</h2>
          <p>{protectedData}</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container d-flex justify-content-center align-items-center vh-100">
      <div className="card p-4 text-center" style={{ width: "400px" }}>
        <h2 className="mb-3">Login</h2>
        <input type="text" className="form-control mb-2" placeholder="Username" disabled />
        <input type="password" className="form-control mb-3" placeholder="Password" disabled />
        <button className="btn btn-primary w-100 mb-3" disabled>
          Login
        </button>
        <h5>Or Login with</h5>
        <button className="btn btn-danger w-100 mb-2" onClick={handleGoogleLogin}>
          Login with Google
        </button>
        <button className="btn btn-info w-100" onClick={handleMicrosoftLogin}>
          Login with Microsoft
        </button>
      </div>
    </div>
  );
};

export default App;
